mapper.update:

 /**runBoard.insertBoardInfo**/
INSERT INTO TN_OPR_BOARD_INFO 
(
	SEQ
	,BOARD_TYPE_CD
	,MENU_CD
	,TITLE
	,CONTENT_DESC
	,FIXED_YN
	,LIKE_YN
	,USER_NM
	,CREATE_ID
	,CREATE_DATETIME
	,UPDATE_ID
	,UPDATE_DATETIME
	,COMMENT_YN
	,HIDDEN_YN
	,CREATE_DATE
	,CRAWLER_IMG_DEC
)
VALUES
(
	#{SEQ}
	,#{BOARD_TYPE_CD}
	,#{MENU_CD}
	,substring(#{TITLE},1,150)
	,#{CONTENT_DESC}
	,(CASE WHEN #{FIXED_YN}='' OR #{FIXED_YN} IS NULL THEN 'N' ELSE 'Y' END) 
	,#{LIKE_YN}
	,#{USER_NM}
	,#{CREATE_ID}
	,DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s')
	,#{UPDATE_ID}
	,DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s')
	,#{COMMENT_YN}
	,(CASE WHEN #{HIDDEN_YN}='' OR #{HIDDEN_YN} IS NULL THEN 'N' ELSE 'Y' END)
	,DATE_FORMAT(NOW(),'%Y%m%d')
	,#{CRAWLER_IMG_DEC}
)
